import axios from "axios";
import { useCallback, useEffect, useState } from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";

interface Priority {
  id: number;
  title: string;
}

interface Ticket {
  id: number;
  title: string;
  description: string;
  priorityId: number;
  statusId: number;
  ticketTypeId: number;
  priorityById: {
    id: number;
    title: string;
  };
  statusById: {
    id: number;
    title: string;
  };
  ticketTypeById: {
    id: number;
    title: string;
  };
  applicationName: string;
  ticketReplies: TicketReply[];
}

interface TicketReply {
  replyId: number;
  tId: number;
  reply: string;
}

interface Type {
  id: number;
  title: string;
}
function SingleTicketView() {
  const [ticketData, setTicketData] = useState<Ticket>();
  const [prioritiesData, setPrioritiesData] = useState<Priority[]>([]);
  const [typesData, setTypesData] = useState<Type[]>([]);

  //   const [isFocused, setIsFocused] = useState(false);

  const [value, setValue] = useState("");
  const [rows, setRows] = useState(3);

  const handleChange = (event) => {
    setValue(event.target.value);
    setRows(event.target.value.split("\n").length); // Adjust rows based on line breaks
  };
  console.log(value);
  const fetchSingleTicketData = useCallback(async () => {
    try {
      const response = await axios.get("/api/tickets/1");
      setTicketData(response.data);
    } catch (error) {
      handleRequestError(error);
    }
  }, []);

  const fetchPrioritiesData = useCallback(async () => {
    try {
      const response = await axios.get("/api/tickets/priorities");
      setPrioritiesData(response.data);
    } catch (error) {
      handleRequestError(error);
    }
  }, []);

  const fetchTicketTypesData = useCallback(async () => {
    try {
      const response = await axios.get("/api/tickets/types");
      setTypesData(response.data);
    } catch (error) {
      handleRequestError(error);
    }
  }, []);

  const updateTicket = useCallback(async () => {
    try {
      await axios.put(`/api/tickets/${ticketData?.id}`, ticketData);
      console.log("Ticket updated successfully");
    } catch (error) {
      handleRequestError(error);
    }
  }, [ticketData]);

  const handleRequestError = (error: unknown) => {
    console.error("An error occurred:", error);
    // Add logic here to handle or display the error message to the user
  };

  useEffect(() => {
    fetchSingleTicketData();
    fetchPrioritiesData();
    fetchTicketTypesData();
  }, [fetchSingleTicketData, fetchPrioritiesData, fetchTicketTypesData]);

  console.log(ticketData);
  console.log(ticketData?.applicationName);
  return (
    <div className="container mx-auto">
      <Formik
        initialValues={{
          applicationName: ticketData?.applicationName || "select",
          priorityId: ticketData?.priorityId || "select",
          ticketTypeId: ticketData?.ticketTypeById?.id || "swlwct",
        }}
        onSubmit={(values, { setSubmitting }) => {
          // Handle form submission here
          console.log(values);
          setSubmitting(false);
        }}
      >
        {({ values, handleChange, handleSubmit, isSubmitting }) => (
          <Form>
            <div>
              <label>New Reply</label>
              <textarea
                className="appearance-none block w-full bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
                value={value}
                onChange={handleChange}
                placeholder="Reply here..."
                rows={rows}
              />
            </div>
            <div className="flex gap-4">
              <div className="flex flex-col flex-1">
                <div className="flex items-center gap-12 pb-4 justify-between">
                  <div className="flex-1 max-w-36">
                    <label className="text-gray-700 text-xs font-bold mb-2">
                      Module
                    </label>
                  </div>
                  <div className="relative flex-1 max-w-36">
                    <Field
                      as="select"
                      name="applicationName"
                      className="block appearance-none w-full bg-gray-200 border border-gray-200 text-gray-700 py-3 px-4 pr-8 rounded leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                      defaultValue={values.applicationName}
                    >
                      <option value="Hr">Hr</option>
                      <option value="Loader">Loader</option>
                      <option value="Finance">Finance</option>
                      <option value="Ingress">Ingress</option>
                      <option value="Cluster">Cluster</option>
                    </Field>
                    <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-700">
                      <svg
                        className="fill-current h-4 w-4"
                        xmlns="http://www.w3.org/2000/svg"
                        viewBox="0 0 20 20"
                      >
                        <path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z" />
                      </svg>
                    </div>
                  </div>
                </div>
                <div className="flex items-center gap-12 pb-4 justify-between">
                  <div className="flex-1 max-w-36">
                    <label className="text-gray-700 text-xs font-bold mb-2 ">
                      Urgent Lvl
                    </label>
                  </div>
                  <div className="relative flex-1 max-w-36">
                    <Field
                      as="select"
                      name="priorityId"
                      className="block appearance-none w-full bg-gray-200 border border-gray-200 text-gray-700 py-3 px-4 pr-8 rounded leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                      defaultValue={values.priorityId || ""}
                    >
                      {prioritiesData.map((priority) => (
                        <option key={priority.id} value={priority.id}>
                          {priority.title}
                        </option>
                      ))}
                    </Field>
                    <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-700">
                      <svg
                        className="fill-current h-4 w-4"
                        xmlns="http://www.w3.org/2000/svg"
                        viewBox="0 0 20 20"
                      >
                        <path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z" />
                      </svg>
                    </div>
                  </div>
                </div>
                <div className="flex items-center gap-12 pb-4 justify-between">
                  <div className="flex-1 max-w-36">
                    <label className="text-gray-700 text-xs font-bold mb-2  ">
                      Type
                    </label>
                  </div>
                  <div className="relative flex-1 max-w-36">
                    <Field
                      as="select"
                      name="ticketTypeId"
                      className="block appearance-none w-full bg-gray-200 border border-gray-200 text-gray-700 py-3 px-4 pr-8 rounded leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                    >
                      {typesData.map((type) => (
                        <option key={type.id} value={type.id}>
                          {type.title}
                        </option>
                      ))}
                    </Field>
                    <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-700">
                      <svg
                        className="fill-current h-4 w-4"
                        xmlns="http://www.w3.org/2000/svg"
                        viewBox="0 0 20 20"
                      >
                        <path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z" />
                      </svg>
                    </div>
                  </div>
                </div>
                <div className="flex items-center gap-12 pb-4">
                  <div className="flex-1 max-w-36">
                    <label className="text-gray-700 text-xs font-bold ">
                      State
                    </label>
                  </div>
                  <div>{ticketData?.statusById?.title}</div>
                </div>
                <div>
                  <label>Description</label>

                  {ticketData ? (
                    <div className="border border-black p-2 mb-2">
                      {ticketData.description}
                    </div>
                  ) : (
                    <div className="border border-black p-2 mb-2">
                      <textarea
                        className="appearance-none block w-full bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
                        value={value}
                        onChange={handleChange}
                        placeholder="Reply here..."
                        rows={rows}
                      />
                    </div>
                  )}
                </div>
              </div>
              <div className="flex-1">
                <label>Replies</label>
                <div className="bg-gray-200 border border-black rounded py-3 px-4 mb-3">
                  {ticketData?.ticketReplies.map((reply) => (
                    <div key={reply.replyId} className="bg-white p-2 mb-4 ">
                      <p>{reply.reply}</p>
                    </div>
                  ))}
                </div>
              </div>
            </div>
            <button
              type="submit"
              disabled={isSubmitting}
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
            >
              Submit
            </button>
          </Form>
        )}
      </Formik>
    </div>
  );
}

export default SingleTicketView;
