import axios from "axios";
import { useCallback, useEffect, useState } from "react";

function SingleTicketView() {
  const [ticketData, setData] = useState([]);
  const [prioritiesData, setPrioritiesData] = useState([]);

  //   const [isFocused, setIsFocused] = useState(false);

  const [value, setValue] = useState("");
  const [rows, setRows] = useState(3);

  const handleChange = (event) => {
    setValue(event.target.value);
    setRows(event.target.value.split("\n").length); // Adjust rows based on line breaks
  };

  const fetchTicketData = useCallback(async () => {
    try {
      const response = await axios.get("/api/ticket/:id");
      setData(response.data);
    } catch (error) {
      handleRequestError(error);
    }
  }, []);

  const fetchPrioritiesData = useCallback(async () => {
    try {
      const response = await axios.get("/api/tickets/priorities");
      setPrioritiesData(response.data);
    } catch (error) {
      handleRequestError(error);
    }
  }, []);

  const handleRequestError = (error) => {
    console.error("An error occurred:", error);
    // Add logic here to handle or display the error message to the user
  };

  useEffect(() => {
    fetchTicketData();
    fetchPrioritiesData();
